// create a table; fill with flags; leave on stack
void push_flags_table (lua_State *L)
{
  int i;
  const int nelem = sizeof(Keys) / sizeof(Keys[0]);
  lua_createtable (L, 0, nelem);
  for (i=0; i<nelem; ++i) {
    push64(L, Vals[i]);
    lua_setfield(L, -2, Keys[i]);
  }
}

